BasicHttpBinding httpBinding = new BasicHttpBinding();\nhttpBinding.UseDefaultWebProxy = false;\n\nNetTcpBinding tcpBinding = new NetTcpBinding();\n\nChannelFactory<IYourServiceContract> httpFactory = new ChannelFactory<IYourServiceContract>(httpBinding, new EndpointAddress("http://yourserviceaddress"));\nChannelFactory<IYourServiceContract> tcpFactory = new ChannelFactory<IYourServiceContract>(tcpBinding, new EndpointAddress("net.tcp://yourserviceaddress"));\n\nIYourServiceContract httpClient = httpFactory.CreateChannel();\nIYourServiceContract tcpClient = tcpFactory.CreateChannel();