public string Submit()\n{\n    try\n    {\n        WebClient client = new WebClient();\n        WebProxy proxy = new WebProxy("http://yourproxyaddress:port"); // Set your proxy address and port\n        proxy.Credentials = new NetworkCredential("username", "password"); // Set your proxy credentials\n        client.Proxy = proxy;\n\n        // Optionally configure ServicePointManager for TLS\n        ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12;\n\n        Byte[] response = client.DownloadData(fogBugzUrl);\n        string responseText = System.Text.Encoding.UTF8.GetString(response);\n        return (responseText == "") ? this.defaultMsg : responseText;\n    }\n    catch (WebException webEx)\n    {\n        // Log webEx details for troubleshooting\n        return $"WebException: {webEx.Message} - {webEx.Status}";\n    }\n    catch (Exception ex)\n    {\n        // Log general exception details for troubleshooting\n        return $"Exception: {ex.Message}";\n    }\n}