using System;\nusing System.Collections.Concurrent;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\nusing System.Threading.Tasks;\n\npublic class ParallelFileSearcher\n{\n    public static IEnumerable<string> GetFiles(string path, List<string> extensions)\n    {\n        var result = new ConcurrentBag<string>();\n\n        Parallel.ForEach(Directory.EnumerateDirectories(path, "*", SearchOption.AllDirectories), directory =>\n        {\n            foreach (var file in Directory.EnumerateFiles(directory))\n            {\n                if (extensions.Contains(Path.GetExtension(file).ToLower()))\n                {\n                    result.Add(file);\n                }\n            }\n        });\n\n        return result;\n    }\n\n    public static void Main()\n    {\n        var extensions = new List<string> { ".log", ".txt", ".dat" };\n        var files = GetFiles(@"C:\path", extensions);\n\n        foreach (var file in files)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}