using System;\nusing Microsoft.Win32;\n\npublic class TouchscreenCalibration\n{\n    public static void SaveCalibrationData(int xLocation, int yLocation, int offset)\n    {\n        try\n        {\n            // Open the registry key where the calibration data is stored\n            RegistryKey key = Registry.LocalMachine.OpenSubKey("HARDWARE\\TOUCH", true);\n\n            if (key == null)\n            {\n                // If the key does not exist, create it\n                key = Registry.LocalMachine.CreateSubKey("HARDWARE\\TOUCH");\n            }\n\n            // Set the calibration values\n            key.SetValue("XLocation", xLocation, RegistryValueKind.DWord);\n            key.SetValue("YLocation", yLocation, RegistryValueKind.DWord);\n            key.SetValue("Offset", offset, RegistryValueKind.DWord);\n\n            // Close the key to ensure the values are written to the registry\n            key.Close();\n\n            // Optionally, save the registry to ensure changes persist after a reboot\n            SaveRegistry();\n\n            Console.WriteLine("Calibration data saved successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error saving calibration data: " + ex.Message);\n        }\n    }\n\n    // P/Invoke to save the registry in WinCE\n    [System.Runtime.InteropServices.DllImport("coredll.dll")]\n    private static extern void RegFlushKey(IntPtr hKey);\n\n    private static void SaveRegistry()\n    {\n        IntPtr HKEY_LOCAL_MACHINE = new IntPtr(unchecked((int)0x80000002));\n        RegFlushKey(HKEY_LOCAL_MACHINE);\n    }\n}