using System;\n   using SimMetrics;\n\n   class Program\n   {\n       static void Main()\n       {\n           // Example usage of a SimMetrics class\n           var metric = new SimMetrics.Metric.Levenstein();\n           string string1 = "string1";\n           string string2 = "string2";\n           float similarity = metric.GetSimilarity(string1, string2);\n\n           Console.WriteLine($"Similarity between '{string1}' and '{string2}' is: {similarity}");\n       }\n   }