protected void GetValuesFromRepeater()\n{\n    // Initialize a list to hold the values\n    List<string[]> valuesList = new List<string[]>();\n\n    // Loop through each item in the repeater\n    foreach (RepeaterItem item in myRepeater.Items)\n    {\n        if (item.ItemType == ListItemType.Item || item.ItemType == ListItemType.AlternatingItem)\n        {\n            // Find the DropDownList and TextBox controls\n            DropDownList ddlAdTitle = (DropDownList)item.FindControl("AdTitle");\n            TextBox txtAdFullName = (TextBox)item.FindControl("AdFullName");\n\n            // Get the selected value of the DropDownList and the text of the TextBox\n            string adTitleValue = ddlAdTitle.SelectedValue;\n            string adFullNameValue = txtAdFullName.Text;\n\n            // Add the values to the multidimensional array (in this case, a list of string arrays)\n            valuesList.Add(new string[] { adTitleValue, adFullNameValue });\n        }\n    }\n\n    // Convert the list to a multidimensional array if needed\n    string[][] multidimensionalArray = valuesList.ToArray();\n}