public abstract class Chart\n{\n    public int ChartId { get; set; }\n    public int ReportId { get; set; }\n    public string Color { get; set; }\n}\n\npublic class LineChart : Chart\n{\n    public int Thickness { get; set; }\n    public string YAxis { get; set; }\n    public string XAxis { get; set; }\n}\n\npublic class PieChart : Chart\n{\n    public int Radius { get; set; }\n}\n\npublic class BarChart : Chart\n{\n    public int Width { get; set; }\n    public string Border { get; set; }\n}\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<Chart> Charts { get; set; }\n    public DbSet<LineChart> LineCharts { get; set; }\n    public DbSet<PieChart> PieCharts { get; set; }\n    public DbSet<BarChart> BarCharts { get; set; }\n\n    protected override void OnModelCreating(ModelBuilder modelBuilder)\n    {\n        modelBuilder.Entity<Chart>()\n            .HasDiscriminator<string>("ChartType")\n            .HasValue<LineChart>("Line")\n            .HasValue<PieChart>("Pie")\n            .HasValue<BarChart>("Bar");\n    }\n}