foreach(DataSet1.DataTable1Row dr in ds.DataTable1)\n{\n    var foundRow = ds.DataTable1.FindById(dr.Id);\n    bool areEqual = true;\n\n    foreach (var column in ds.DataTable1.Columns)\n    {\n        if (!dr[column].Equals(foundRow[column]))\n        {\n            areEqual = false;\n            break;\n        }\n    }\n\n    if (!areEqual)\n    {\n        Console.Write(dr.Id);\n    }\n}