using System;\nusing System.IO;\nusing System.Web;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic class PdfHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string filePath = context.Server.MapPath("~/GeneratedFiles/tempfile.pdf");\n\n        // Create the PDF file\n        using (Document doc = new Document())\n        {\n            PdfWriter.GetInstance(doc, new FileStream(filePath, FileMode.Create));\n            doc.Open();\n            doc.Add(new Paragraph("Hello, World!"));\n            doc.Close();\n        }\n\n        // Stream the PDF to the browser\n        context.Response.ContentType = "application/pdf";\n        context.Response.AppendHeader("Content-Disposition", "attachment; filename=tempfile.pdf");\n        context.Response.WriteFile(filePath);\n        context.Response.Flush();\n\n        // Delete the PDF file\n        if (File.Exists(filePath))\n        {\n            try\n            {\n                File.Delete(filePath);\n            }\n            catch (Exception ex)\n            {\n                // Log exception\n                // context.Response.Write("Error deleting file: " + ex.Message);\n            }\n        }\n\n        context.Response.End();\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}