from t in db.Import_Tables\njoin h in db.Import_CheckoutHistories on t.Import_CheckoutHistoryID equals h.Import_CheckoutHistoryID into historyGroup\nfrom h in historyGroup.DefaultIfEmpty()\njoin u in db.System_Users on h.System_UserID equals u.System_UserID into userGroup\nfrom u in userGroup.DefaultIfEmpty()\nselect new {\n   CheckedOutUser = (!t.IsCheckedOut) ? "--" : u.FirstName + " " + u.LastName,\n   CheckedOutUserID = (!t.IsCheckedOut) ? 0 : (int?)u.System_UserID\n};