using System.Net;\nusing System.Net.Mail;\nusing System.Configuration;\n\nvar smtpClient = new SmtpClient\n{\n    Host = ConfigurationManager.AppSettings["smtpServer"],\n    Port = Convert.ToInt32(ConfigurationManager.AppSettings["smtpPort"]),\n    EnableSsl = true, // If SSL is required\n    Credentials = new NetworkCredential(ConfigurationManager.AppSettings["smtpUsername"], ConfigurationManager.AppSettings["smtpPassword"])\n};\n\nvar mailMessage = new MailMessage\n{\n    From = new MailAddress("your_email@example.com"),\n    Subject = "Subject",\n    Body = "Email body text",\n    IsBodyHtml = false\n};\n\nmailMessage.To.Add("recipient@example.com");\n\n// Ensure that the attachment is properly added\nstring attachmentPath = "path_to_your_temp_file.csv";\nAttachment attachment = new Attachment(attachmentPath, "text/csv");\nattachment.ContentDisposition.FileName = "your_file.csv";\nmailMessage.Attachments.Add(attachment);\n\ntry\n{\n    smtpClient.Send(mailMessage);\n    Console.WriteLine("Email sent successfully.");\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("Error sending email: " + ex.Message);\n}