public void ProcessRequest(HttpContext context)\n   {\n       context.Response.ContentType = "application/pdf"; // or the appropriate MIME type\n       context.Response.AddHeader("Content-Disposition", "inline; filename=document.pdf");\n       context.Response.Cache.SetCacheability(HttpCacheability.NoCache);\n       context.Response.Cache.SetNoStore();\n       context.Response.Cache.SetExpires(DateTime.UtcNow.AddMinutes(-1));\n       context.Response.Cache.SetValidUntilExpires(false);\n\n       // Write the document to the response stream\n       byte[] documentBytes = ...; // your document bytes\n       context.Response.OutputStream.Write(documentBytes, 0, documentBytes.Length);\n       context.Response.End();\n   }