using System;\nusing System.Web.UI.WebControls;\n\nnamespace MyNamespace\n{\n    public partial class MyPage : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                AddRequiredFieldValidator();\n            }\n        }\n\n        private void AddRequiredFieldValidator()\n        {\n            // Find the DropDownList within the UserControl\n            DropDownList ddlExample = (DropDownList)MyUserControl1.FindControl("ddlExample");\n\n            if (ddlExample != null)\n            {\n                // Create a new RequiredFieldValidator\n                RequiredFieldValidator validator = new RequiredFieldValidator();\n                validator.ID = "rfvExample";\n                validator.ControlToValidate = ddlExample.ID;\n                validator.ErrorMessage = "Please select an option.";\n                validator.InitialValue = ""; // Assuming the initial value to be empty string\n                validator.Display = ValidatorDisplay.Dynamic;\n\n                // Add the validator to the page\n                this.form1.Controls.Add(validator);\n            }\n        }\n    }\n}