using System;\nusing System.Windows.Forms;\n\npublic partial class MyUserControl : UserControl\n{\n    public event EventHandler<string> ValidationErrorOccurred;\n\n    public MyUserControl()\n    {\n        InitializeComponent();\n    }\n\n    private void ValidateInputs()\n    {\n        // Assuming textBox1 is a TextBox control in the UserControl\n        if (string.IsNullOrWhiteSpace(textBox1.Text))\n        {\n            OnValidationErrorOccurred("Input cannot be empty.");\n        }\n    }\n\n    protected virtual void OnValidationErrorOccurred(string errorMessage)\n    {\n        ValidationErrorOccurred?.Invoke(this, errorMessage);\n    }\n}