using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class OracleTransactionExample\n{\n    public void PerformInsertsWithTransaction()\n    {\n        string connectionString = "Your Oracle Connection String Here";\n        \n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n            OracleTransaction transaction = connection.BeginTransaction();\n\n            try\n            {\n                for (int i = 0; i < 10; i++)\n                {\n                    string insertCommandText = "INSERT INTO YourTable (Column1, Column2) VALUES (:Value1, :Value2)";\n                    using (OracleCommand command = new OracleCommand(insertCommandText, connection))\n                    {\n                        command.Parameters.Add(new OracleParameter(":Value1", "Value1"));\n                        command.Parameters.Add(new OracleParameter(":Value2", "Value2"));\n                        command.ExecuteNonQuery();\n                    }\n                }\n\n                // Commit the transaction if all inserts are successful\n                transaction.Commit();\n            }\n            catch (Exception ex)\n            {\n                // Rollback the transaction in case of error\n                transaction.Rollback();\n                // Handle the exception as needed\n                Console.WriteLine(ex.Message);\n            }\n        }\n    }\n}