using System.Threading.Tasks;\n\npublic async Task SendEmailToSubscribersAsync(List<string> subscribers, string subject, string body)\n{\n    SmtpClient smtpClient = new SmtpClient("your_smtp_server");\n    smtpClient.Credentials = new NetworkCredential("your_username", "your_password");\n\n    foreach (string email in subscribers)\n    {\n        MailMessage mailMessage = new MailMessage();\n        mailMessage.From = new MailAddress("your_email@example.com");\n        mailMessage.To.Add(new MailAddress(email));\n        mailMessage.Subject = subject;\n        mailMessage.Body = body;\n\n        try\n        {\n            await smtpClient.SendMailAsync(mailMessage);\n            await Task.Delay(100); // Throttle by waiting 100ms between sends\n        }\n        catch (Exception ex)\n        {\n            // Handle the exception (log it, retry, etc.)\n            Console.WriteLine($"Error sending email to {email}: {ex.Message}");\n        }\n    }\n}