using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string input = "Hello, World!";\n        string pattern = @"\b\w+\b"; // Matches individual words\n\n        // Pass the original string as a parameter to the callback\n        string result = Regex.Replace(input, pattern, match => ReplaceMatch(match, input));\n\n        Console.WriteLine(result); // Output: [Hello], [World]!\n    }\n\n    public static string ReplaceMatch(Match match, string originalString)\n    {\n        // Example replacement logic that uses the original string\n        return $"[{match.Value}]"; \n    }\n}