using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string input = "Hello, World!";\n        string pattern = @"\b\w+\b"; // Matches individual words\n\n        // Callback function for replacement\n        string result = Regex.Replace(input, pattern, new MatchEvaluator(ReplaceMatch));\n\n        Console.WriteLine(result); // Output: [Hello], [World]!\n    }\n\n    public static string ReplaceMatch(Match match)\n    {\n        return $"[{match.Value}]"; // Example replacement logic\n    }\n}