using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TripleDESDecryptor\n{\n    public static string Decrypt(string base64CipherText, string key, string iv)\n    {\n        byte[] keyBytes = Encoding.UTF8.GetBytes(key);\n        byte[] ivBytes = Encoding.UTF8.GetBytes(iv);\n        byte[] cipherTextBytes = Convert.FromBase64String(base64CipherText);\n\n        using (TripleDESCryptoServiceProvider tripleDES = new TripleDESCryptoServiceProvider())\n        {\n            tripleDES.Key = keyBytes;\n            tripleDES.IV = ivBytes;\n            tripleDES.Mode = CipherMode.CBC;\n            tripleDES.Padding = PaddingMode.PKCS7;\n\n            using (ICryptoTransform decryptor = tripleDES.CreateDecryptor())\n            using (MemoryStream ms = new MemoryStream(cipherTextBytes))\n            using (CryptoStream cs = new CryptoStream(ms, decryptor, CryptoStreamMode.Read))\n            using (StreamReader sr = new StreamReader(cs))\n            {\n                return sr.ReadToEnd();\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string encryptedText = "YOUR_BASE64_ENCRYPTED_STRING";\n        string key = "123456789012345678901234";  // 24-byte key for Triple DES\n        string iv = "12345678";  // 8-byte IV for CBC mode\n\n        string decryptedText = Decrypt(encryptedText, key, iv);\n        Console.WriteLine("Decrypted text: " + decryptedText);\n    }\n}