using MetadataExtractor;\nusing MetadataExtractor.Formats.Exif;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\n\npublic void CopyExifMetadata(string tiffPath, string jpegPath)\n{\n    // Load the TIFF image and read its metadata\n    var directories = ImageMetadataReader.ReadMetadata(tiffPath);\n    var exifDirectory = directories.OfType<ExifSubIfdDirectory>().FirstOrDefault();\n\n    if (exifDirectory == null)\n    {\n        throw new InvalidOperationException("No EXIF metadata found in the TIFF image.");\n    }\n\n    using (var tiffImage = Image.FromFile(tiffPath))\n    {\n        using (var jpegImage = new Bitmap(tiffImage))\n        {\n            foreach (var propertyItem in tiffImage.PropertyItems)\n            {\n                jpegImage.SetPropertyItem(propertyItem);\n            }\n\n            // Save the JPEG image with the copied EXIF metadata\n            var jpegEncoder = GetEncoder(ImageFormat.Jpeg);\n            var encoderParameters = new EncoderParameters(1);\n            encoderParameters.Param[0] = new EncoderParameter(Encoder.Quality, 85L);\n            jpegImage.Save(jpegPath, jpegEncoder, encoderParameters);\n        }\n    }\n}\n\nprivate ImageCodecInfo GetEncoder(ImageFormat format)\n{\n    return ImageCodecInfo.GetImageDecoders().FirstOrDefault(codec => codec.FormatID == format.Guid);\n}