using System;\nusing System.Data;\nusing System.Data.OracleClient;\n\npublic void ExecuteOracleCommand()\n{\n    string commandText = @"\n        DECLARE\n            cnt NUMBER;\n        BEGIN\n            SELECT COUNT(*) INTO cnt FROM all_tables WHERE table_name = 'TABLE_A';\n            \n            IF cnt = 1 THEN\n                EXECUTE IMMEDIATE 'TRUNCATE TABLE TABLE_A';\n                EXECUTE IMMEDIATE 'DROP TABLE TABLE_A';\n            END IF;\n            \n            EXECUTE IMMEDIATE 'CREATE TABLE TABLE_A AS\n                (SELECT DISTINCT v.ID, g.ext_id FROM VIEW_A v\n                 JOIN TABLE_B B ON v.id = B.Id\n                 WHERE YEAR1 = ''2008'')';\n        END;";\n    \n    using (OracleConnection conn = new OracleConnection("Your_Connection_String_Here"))\n    {\n        OracleCommand createTempTable = new OracleCommand(commandText, conn)\n        {\n            CommandType = CommandType.Text\n        };\n        \n        conn.Open();\n        \n        try\n        {\n            createTempTable.ExecuteNonQuery();\n        }\n        catch (Exception ex)\n        {\n            // Handle exception as necessary\n            throw;\n        }\n        finally\n        {\n            conn.Close();\n        }\n    }\n}