using System.Web.Mvc;\nusing System.IO;\n\npublic class YourController : Controller\n{\n    public ActionResult GetPartialViewHtml()\n    {\n        var model = /* Your model data here */;\n        string partialViewHtml = RenderPartialViewToString("_PartialView", model);\n\n        return Json(new { html = partialViewHtml }, JsonRequestBehavior.AllowGet);\n    }\n\n    private string RenderPartialViewToString(string viewName, object model)\n    {\n        ViewData.Model = model;\n        using (var sw = new StringWriter())\n        {\n            var viewResult = ViewEngines.Engines.FindPartialView(ControllerContext, viewName);\n            var viewContext = new ViewContext(ControllerContext, viewResult.View, ViewData, TempData, sw);\n            viewResult.View.Render(viewContext, sw);\n            return sw.GetStringBuilder().ToString();\n        }\n    }\n}