using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class SoapClient\n{\n    private static async Task<string> CallSoapWebService(string url, string action, string soapEnvelope)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            HttpRequestMessage request = new HttpRequestMessage()\n            {\n                Method = HttpMethod.Post,\n                RequestUri = new Uri(url),\n                Content = new StringContent(soapEnvelope, Encoding.UTF8, "text/xml")\n            };\n\n            request.Headers.Add("SOAPAction", action);\n\n            HttpResponseMessage response = await client.SendAsync(request);\n            response.EnsureSuccessStatusCode();\n\n            return await response.Content.ReadAsStringAsync();\n        }\n    }\n\n    public static async Task Main(string[] args)\n    {\n        string url = "http://yourwebserviceurl";\n        string action = "yourSoapAction";\n        string soapEnvelope = @"<soapenv:Envelope xmlns:soapenv='http://schemas.xmlsoap.org/soap/envelope/' xmlns:web='http://yourwebserviceurl'>\n                                    <soapenv:Header/>\n                                    <soapenv:Body>\n                                        <web:YourMethod>\n                                            <!-- Your method parameters here -->\n                                        </web:YourMethod>\n                                    </soapenv:Body>\n                                </soapenv:Envelope>";\n\n        string response = await CallSoapWebService(url, action, soapEnvelope);\n        Console.WriteLine(response);\n    }\n}