using System;\nusing System.Web;\n\nnamespace YourNamespace\n{\n    public class DomainValidator\n    {\n        private static readonly string[] AllowedDomains = { "yourdomain.com", "anotherdomain.com" };\n\n        public static void ValidateDomain()\n        {\n            string currentDomain = HttpContext.Current.Request.Url.Host.ToLower();\n\n            foreach (string allowedDomain in AllowedDomains)\n            {\n                if (currentDomain.EndsWith(allowedDomain.ToLower()))\n                {\n                    return;\n                }\n            }\n\n            throw new UnauthorizedAccessException("This DLL can only be used on specified domains.");\n        }\n    }\n}