using NHibernate;\nusing System.Collections.Generic;\n\npublic class EventItem\n{\n    public virtual IList<string> AudienceList { get; set; }\n}\n\npublic bool IsAudienceReferenced(ISession session, string audienceId)\n{\n    var hqlQuery = @"select count(e) from EventItem e where :audienceId in elements(e.AudienceList)";\n    var count = session.CreateQuery(hqlQuery)\n                       .SetParameter("audienceId", audienceId)\n                       .UniqueResult<int>();\n\n    return count > 0;\n}