// Assuming you have a data source set up\nvar dataSource = new List<MyData>\n{\n    new MyData { Id = 1, Name = "Option 1" },\n    new MyData { Id = 2, Name = "Option 2" },\n    new MyData { Id = 3, Name = "Option 3" }\n};\n\n// Set the DataSource of the UltraCombo\nultraCombo.DataSource = dataSource;\nultraCombo.DisplayMember = "Name";\nultraCombo.ValueMember = "Id";\n\n// Pre-select a value by setting the Value property\nultraCombo.Value = 2; // Assuming you want to pre-select "Option 2"