using (var connection = new SqlConnection("your_connection_string"))\n    {\n        connection.Open();\n        using (var command = new SqlCommand("dbo.MyStoredProc", connection))\n        {\n            command.CommandType = CommandType.StoredProcedure;\n\n            var list1 = new DataTable();\n            list1.Columns.Add("Value", typeof(int));\n            list1.Rows.Add(1);\n            list1.Rows.Add(2);\n            list1.Rows.Add(3);\n\n            var list2 = new DataTable();\n            list2.Columns.Add("Value", typeof(int));\n            list2.Rows.Add(10);\n            list2.Rows.Add(20);\n            list2.Rows.Add(30);\n\n            var param1 = command.Parameters.AddWithValue("@List1", list1);\n            param1.SqlDbType = SqlDbType.Structured;\n            param1.TypeName = "dbo.IntList";\n\n            var param2 = command.Parameters.AddWithValue("@List2", list2);\n            param2.SqlDbType = SqlDbType.Structured;\n            param2.TypeName = "dbo.IntList";\n\n            command.ExecuteNonQuery();\n        }\n    }