public List<Setting> GetAllSettings()\n   {\n       using (var context = new YourDbContext())\n       {\n           var userSettings = context.Settings.ToList();\n           var systemSettings = LoadSystemSettings();\n\n           // Merge settings, giving priority to user settings\n           var allSettings = systemSettings\n               .Where(sys => !userSettings.Any(user => user.Key == sys.Key))\n               .Concat(userSettings)\n               .ToList();\n\n           return allSettings;\n       }\n   }