public List<Author> QueryAuthorsByXmlField(MyDataContext context, string xpath, string expectedValue) {\n        string query = @"\n            SELECT [ID], [LastName], [FirstName], [XmlField]\n            FROM [Author]\n            WHERE XmlField.value(@xpath, 'nvarchar(max)') = @expectedValue";\n        \n        var command = context.GetCommand(context.ExecuteQuery<Author>(query, new object[] { \n            new SqlParameter("@xpath", xpath),\n            new SqlParameter("@expectedValue", expectedValue)\n        }));\n        \n        var authors = new List<Author>();\n        using (var reader = command.ExecuteReader()) {\n            while (reader.Read()) {\n                authors.Add(new Author {\n                    ID = reader.GetInt32(0),\n                    LastName = reader.GetString(1),\n                    FirstName = reader.GetString(2),\n                    XmlField = reader.GetString(3)\n                });\n            }\n        }\n        return authors;\n    }