using System.IO;\nusing System.Windows.Documents;\nusing HtmlToXaml;\n\nstring html = "<p>Your HTML content here</p>";\n\n// Convert HTML to XAML\nstring xaml = HtmlToXamlConverter.ConvertHtmlToXaml(html, true);\n\n// Optional: To use the XAML in a RichTextBox\nFlowDocument flowDocument = (FlowDocument)System.Windows.Markup.XamlReader.Parse(xaml);\nRichTextBox richTextBox = new RichTextBox();\nrichTextBox.Document = flowDocument;\n\n// Save XAML to a file (if needed)\nFile.WriteAllText("output.xaml", xaml);