string sql = @"\n    SELECT \n        cc.*, c.*, o.*, od.*\n    FROM \n        CustomerCategory cc\n    INNER JOIN \n        Customer c ON cc.CustomerCategoryID = c.CustomerCategoryID\n    INNER JOIN \n        [Order] o ON c.CustomerID = o.CustomerID\n    INNER JOIN \n        OrderDetail od ON o.OrderID = od.OrderID\n    WHERE \n        o.OrderDate BETWEEN '2008-05-05' AND '2008-12-31'";