using System;\nusing System.IO;\nusing System.Web;\nusing OfficeOpenXml;\nusing OfficeOpenXml.Drawing;\n\npublic void RenderImageToExcel(HttpResponse response, string imagePath)\n{\n    using (ExcelPackage excelPackage = new ExcelPackage())\n    {\n        // Create a worksheet\n        ExcelWorksheet worksheet = excelPackage.Workbook.Worksheets.Add("Sheet1");\n\n        // Load the image\n        Bitmap image = new Bitmap(imagePath);\n        ExcelPicture picture = worksheet.Drawings.AddPicture("MyImage", image);\n\n        // Position the image in the worksheet\n        picture.SetPosition(1, 0, 1, 0); // Row, RowOffsetPixel, Column, ColumnOffsetPixel\n\n        // Prepare the response\n        response.Clear();\n        response.ContentType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";\n        response.AddHeader("content-disposition", $"attachment; filename=ImageInExcel.xlsx");\n\n        // Save the Excel package to the response stream\n        using (MemoryStream stream = new MemoryStream())\n        {\n            excelPackage.SaveAs(stream);\n            stream.WriteTo(response.OutputStream);\n        }\n\n        response.End();\n    }\n}