public static class HttpResponseExtensions\n{\n    public static void RedirectWithoutPort(this HttpResponse response, string url)\n    {\n        Uri uri = new Uri(url);\n        string cleanUrl = uri.GetLeftPart(UriPartial.Authority) + uri.PathAndQuery;\n        response.Redirect(cleanUrl, true);\n    }\n}\n\n// Usage in your code\nResponse.RedirectWithoutPort("http://localhost:801/somepath");