public class HomeController : Controller\n{\n    // Action to load the initial view\n    public ActionResult Index()\n    {\n        ViewBag.Countries = GetCountries();\n        return View();\n    }\n\n    // Action to get the regions based on the selected country\n    [HttpPost]\n    public JsonResult GetRegions(int countryId)\n    {\n        var regions = GetRegionsByCountry(countryId);\n        return Json(regions);\n    }\n\n    // Example methods to fetch countries and regions (replace with actual database calls)\n    private List<Country> GetCountries()\n    {\n        return new List<Country>\n        {\n            new Country { Id = 1, Name = "Country 1" },\n            new Country { Id = 2, Name = "Country 2" }\n        };\n    }\n\n    private List<Region> GetRegionsByCountry(int countryId)\n    {\n        var regions = new List<Region>\n        {\n            new Region { Id = 1, Name = "Region 1", CountryId = 1 },\n            new Region { Id = 2, Name = "Region 2", CountryId = 1 },\n            new Region { Id = 3, Name = "Region 3", CountryId = 2 },\n            new Region { Id = 4, Name = "Region 4", CountryId = 2 }\n        };\n        return regions.Where(r => r.CountryId == countryId).ToList();\n    }\n}\n\npublic class Country\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class Region\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public int CountryId { get; set; }\n}