protected void Wizard_NextButtonClick(object sender, WizardNavigationEventArgs e)\n{\n    // Perform validation\n    Page.Validate("wizardGroup");\n\n    // Check if the page is valid\n    if (!Page.IsValid)\n    {\n        // Cancel the wizard's step change\n        e.Cancel = true;\n    }\n}\n\nvoid mustUploadAnImageValidator_ServerValidate(object source, ServerValidateEventArgs args)\n{\n    // Your server-side validation logic here\n    // Set args.IsValid to true if valid, false if not\n    args.IsValid = IsImageSelected(args.Value);\n}\n\nprivate bool IsImageSelected(string value)\n{\n    // Implement your validation logic here\n    // Return true if valid, false if not\n    return !string.IsNullOrEmpty(value) && IsValidImage(value);\n}