using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            SmtpClient client = new SmtpClient("your_exchange_server_address");\n            client.Port = 25; // or the port your SMTP server is using\n            client.Credentials = new NetworkCredential("username", "password"); // If authentication is required\n\n            MailMessage mailMessage = new MailMessage();\n            mailMessage.From = new MailAddress("your_email@domain.com");\n            mailMessage.To.Add("recipient@externaldomain.com");\n            mailMessage.Subject = "Test Mail";\n            mailMessage.Body = "This is a test email";\n\n            client.Send(mailMessage);\n            Console.WriteLine("Mail sent successfully.");\n        }\n        catch (SmtpFailedRecipientException ex)\n        {\n            Console.WriteLine("Failed to deliver message to " + ex.FailedRecipient + ": " + ex.Message);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception caught: " + ex.Message);\n        }\n    }\n}