using System;\nusing System.Diagnostics.Tracing;\n\npublic class MyEventSource : EventSource\n{\n    public static MyEventSource Log = new MyEventSource();\n    \n    [Event(1, Message = "Application Failure: {0}", Level = EventLevel.Error)]\n    public void Failure(string message) => WriteEvent(1, message);\n    \n    [Event(2, Message = "Application Information: {0}", Level = EventLevel.Informational)]\n    public void Info(string message) => WriteEvent(2, message);\n    \n    [Event(3, Message = "User Interaction: {0}", Level = EventLevel.Informational)]\n    public void UserInteraction(string action) => WriteEvent(3, action);\n}\n\nclass Program\n{\n    static void Main()\n    {\n        MyEventSource.Log.Info("Application started");\n        MyEventSource.Log.UserInteraction("Button clicked");\n        MyEventSource.Log.Failure("Unhandled Exception");\n\n        Console.WriteLine("Press Enter to exit");\n        Console.ReadLine();\n    }\n}