using System;\nusing System.Windows.Forms;\n\npublic class FolderBrowserExample\n{\n    [STAThread]\n    public static void Main()\n    {\n        using (FolderBrowserDialog folderBrowserDialog = new FolderBrowserDialog())\n        {\n            // Set the initial directory to the Desktop folder\n            folderBrowserDialog.SelectedPath = Environment.GetFolderPath(Environment.SpecialFolder.Desktop);\n\n            DialogResult result = folderBrowserDialog.ShowDialog();\n            if (result == DialogResult.OK)\n            {\n                // The selected folder path\n                string selectedPath = folderBrowserDialog.SelectedPath;\n                Console.WriteLine("Selected folder: " + selectedPath);\n            }\n        }\n    }\n}