using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing System.Web.UI;\n\npublic partial class SendSMS : Page\n{\n    protected void bText_Click(object sender, EventArgs e)\n    {\n        string XML = "<?xml version=\"1.0\"?>"\n                    + "<methodCall>"\n                    + "<methodName>send</methodName>"\n                    + "    <params>"\n                    + "        <param>"\n                    + "            <value><string>YOUR_API_KEY</string></value>"\n                    + "        </param>"\n                    + "        <param>"\n                    + "            <value><string>service@example.com</string></value>"\n                    + "       </param>"\n                    + "       <param>"\n                    + "           <value><string>5555555555</string></value>" // Ensure the number format is correct\n                    + "       </param>"\n                    + "       <param>"\n                    + "          <value><string>Test Message</string></value>"\n                    + "      </param>"\n                    + "  </params>"\n                    + "</methodCall>";\n        SendText("https://api.pennysms.com/xmlrpc", XML); // Use HTTPS for security\n    }\n\n    public void SendText(string url, string parameters)\n    {\n        WebRequest request = WebRequest.Create(url);\n        request.Method = "POST";\n        byte[] byteArray = Encoding.UTF8.GetBytes(parameters);\n        request.ContentType = "text/xml";\n        request.ContentLength = byteArray.Length;\n\n        using (Stream dataStream = request.GetRequestStream())\n        {\n            dataStream.Write(byteArray, 0, byteArray.Length);\n        }\n\n        using (WebResponse response = request.GetResponse())\n        {\n            using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n            {\n                string responseFromServer = reader.ReadToEnd();\n                // Process the response if needed\n                Console.WriteLine(responseFromServer);\n            }\n        }\n    }\n}