public class CustomControl : SomeBaseControl\n{\n    protected override void CreateChildControls()\n    {\n        base.CreateChildControls();\n\n        // Create and add your dynamic controls here\n        Button newButton = new Button();\n        newButton.ID = "newButton";\n        newButton.Text = "Click Me";\n        this.Controls.Add(newButton);\n\n        // Ensure controls are created and added to the tree\n        EnsureChildControls();\n    }\n\n    protected override void OnInit(EventArgs e)\n    {\n        base.OnInit(e);\n        // Call EnsureChildControls to ensure controls are created during initialization\n        EnsureChildControls();\n    }\n}