using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        string siteName = "Default Web Site"; // Adjust according to your site\n        string virtualDirectoryName = "MedicatiebeheerFittests";\n\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site == null)\n            {\n                Console.WriteLine($"Site '{siteName}' not found.");\n                return;\n            }\n\n            Application app = site.Applications["/"];\n            VirtualDirectory vDir = app.VirtualDirectories["/" + virtualDirectoryName];\n            if (vDir == null)\n            {\n                Console.WriteLine($"Virtual directory '{virtualDirectoryName}' not found.");\n                return;\n            }\n\n            app.VirtualDirectories.Remove(vDir);\n            serverManager.CommitChanges();\n            Console.WriteLine("Virtual directory deleted successfully.");\n        }\n    }\n}