using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic static class NumberFormatter\n{\n    public static string FormatInts(int[] ints)\n    {\n        if (ints == null || ints.Length == 0)\n            return string.Empty;\n\n        Array.Sort(ints);\n        var result = new List<string>();\n        int start = ints[0], end = ints[0];\n\n        for (int i = 1; i < ints.Length; i++)\n        {\n            if (ints[i] == end + 1)\n            {\n                end = ints[i];\n            }\n            else\n            {\n                result.Add(FormatRange(start, end));\n                start = end = ints[i];\n            }\n        }\n        result.Add(FormatRange(start, end));\n\n        return string.Join(", ", result);\n    }\n\n    private static string FormatRange(int start, int end)\n    {\n        return start == end ? start.ToString() : $"{start}-{end}";\n    }\n}\n\n// Example usage\nclass Program\n{\n    static void Main()\n    {\n        int[] numbers = { 1, 2, 3, 5, 7, 9, 10, 11, 12, 14 };\n        Console.WriteLine(NumberFormatter.FormatInts(numbers));\n        // Output: "1-3, 5, 7, 9-12, 14"\n    }\n}