using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class SimpleProxy\n{\n    private static void Main(string[] args)\n    {\n        // Create an HttpListener instance to listen for incoming requests.\n        HttpListener listener = new HttpListener();\n        listener.Prefixes.Add("http://localhost:8080/");\n        listener.Start();\n        Console.WriteLine("Proxy started. Listening on http://localhost:8080/");\n\n        while (true)\n        {\n            // Wait for an incoming request.\n            HttpListenerContext context = listener.GetContext();\n            HttpListenerRequest request = context.Request;\n            HttpListenerResponse response = context.Response;\n\n            // Forward the request to the target server.\n            HttpWebRequest webRequest = (HttpWebRequest)WebRequest.Create(request.Url);\n            webRequest.Method = request.HttpMethod;\n            using (HttpWebResponse webResponse = (HttpWebResponse)webRequest.GetResponse())\n            {\n                // Read the response from the target server.\n                using (StreamReader reader = new StreamReader(webResponse.GetResponseStream()))\n                {\n                    string responseFromServer = reader.ReadToEnd();\n\n                    // Modify the HTML content if needed.\n                    string modifiedContent = ModifyHtmlContent(responseFromServer);\n\n                    // Write the modified content to the response stream.\n                    byte[] buffer = Encoding.UTF8.GetBytes(modifiedContent);\n                    response.ContentLength64 = buffer.Length;\n                    response.OutputStream.Write(buffer, 0, buffer.Length);\n                }\n            }\n\n            // Close the response.\n            response.Close();\n        }\n    }\n\n    private static string ModifyHtmlContent(string htmlContent)\n    {\n        // Implement your HTML modification logic here.\n        // For example, we can add a custom message at the end of the body.\n        return htmlContent.Replace("</body>", "<p>Modified by proxy</p></body>");\n    }\n}