using System;\nusing System.Management;\nusing System.Windows.Forms;\n\nnamespace VideoCodecs {\n    public partial class MainForm : Form {\n        public MainForm() {\n            InitializeComponent();\n            ListVideoCodecs();\n        }\n\n        private void ListVideoCodecs() {\n            try {\n                ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM Win32_CodecFile WHERE Group = 'Video'");\n\n                foreach (ManagementObject codec in searcher.Get()) {\n                    string name = codec["Name"]?.ToString();\n                    string description = codec["Description"]?.ToString();\n\n                    listBoxCodecs.Items.Add($"Name: {name}, Description: {description}");\n                }\n            } catch (Exception ex) {\n                MessageBox.Show($"An error occurred while listing video codecs: {ex.Message}");\n            }\n        }\n    }\n}