using System;\nusing System.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to the custom config file\n        string customConfigPath = "custom.config";\n\n        // Load the custom config file\n        ExeConfigurationFileMap configMap = new ExeConfigurationFileMap();\n        configMap.ExeConfigFilename = customConfigPath;\n        Configuration customConfig = ConfigurationManager.OpenMappedExeConfiguration(configMap, ConfigurationUserLevel.None);\n\n        // Access settings from the custom configuration\n        string customSettingValue = customConfig.AppSettings.Settings["CustomSetting"].Value;\n\n        Console.WriteLine("Custom setting value: " + customSettingValue);\n    }\n}