public class RemoteCommandServiceFacade\n{\n    private readonly RemoteCommandServiceProxy _proxy;\n\n    public RemoteCommandServiceFacade()\n    {\n        _proxy = new RemoteCommandServiceProxy();\n    }\n\n    public string ExecuteCommand(string command)\n    {\n        var client = _proxy.CreateProxy();\n        return client.ExecuteCommand(command); // Assuming ExecuteCommand is a method in IRemoteCommandService\n    }\n}