using System;\n\npublic class SpeakToMeEventArgs : EventArgs\n{\n    public string Message { get; }\n\n    public SpeakToMeEventArgs(string message)\n    {\n        Message = message;\n    }\n}\n\npublic delegate void SpeakToMeHandler(object sender, SpeakToMeEventArgs e);\n\npublic class Speaker\n{\n    public event SpeakToMeHandler SpeakToMe;\n\n    private Tweeter tweeter;\n    private Woofer woofer;\n\n    public Speaker()\n    {\n        tweeter = new Tweeter();\n        woofer = new Woofer();\n\n        tweeter.SpeakToMe += Tweeter_SpeakToMe;\n        woofer.SpeakToMe += Woofer_SpeakToMe;\n    }\n\n    protected virtual void OnSpeakToMe(string message)\n    {\n        SpeakToMe?.Invoke(this, new SpeakToMeEventArgs(DateTime.Now.ToString() + " - " + message));\n    }\n\n    private void Tweeter_SpeakToMe(object sender, SpeakToMeEventArgs e)\n    {\n        Console.WriteLine("The tweeter is speaking: " + e.Message);\n        this.OnSpeakToMe("Tweeter rocking out [" + e.Message + "]");\n    }\n\n    private void Woofer_SpeakToMe(object sender, SpeakToMeEventArgs e)\n    {\n        Console.WriteLine("The woofer is speaking: " + e.Message);\n        this.OnSpeakToMe("Woofer booming out [" + e.Message + "]");\n    }\n}\n\npublic class Tweeter\n{\n    public event SpeakToMeHandler SpeakToMe;\n\n    public void Speak(string message)\n    {\n        SpeakToMe?.Invoke(this, new SpeakToMeEventArgs(message));\n    }\n}\n\npublic class Woofer\n{\n    public event SpeakToMeHandler SpeakToMe;\n\n    public void Speak(string message)\n    {\n        SpeakToMe?.Invoke(this, new SpeakToMeEventArgs(message));\n    }\n}\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        Speaker speaker = new Speaker();\n        speaker.SpeakToMe += Speaker_SpeakToMe;\n\n        // Fire events\n        speaker.tweeter.Speak("Tweeter message");\n        speaker.woofer.Speak("Woofer message");\n    }\n\n    private static void Speaker_SpeakToMe(object sender, SpeakToMeEventArgs e)\n    {\n        Console.WriteLine("Speaker event: " + e.Message);\n    }\n}