// Setting the cookie\n   HttpCookie userCookie = new HttpCookie("UserInfo");\n   userCookie.Values["Username"] = "Mike";\n   userCookie.Values["UserID"] = "12345";\n   userCookie.Values["Email"] = "mike@example.com";\n   userCookie.Expires = DateTime.Now.AddMonths(1); // Set a long expiration date\n   Response.Cookies.Add(userCookie);\n\n   // Retrieving the cookie\n   if (Request.Cookies["UserInfo"] != null)\n   {\n       string username = Request.Cookies["UserInfo"]["Username"];\n       string userID = Request.Cookies["UserInfo"]["UserID"];\n       string email = Request.Cookies["UserInfo"]["Email"];\n   }