using System;\nusing System.Runtime.InteropServices;\nusing Miner.Interop;\n\nclass Program\n{\n    [STAThread]\n    private static void Main()\n    {\n        // Initialize COM\n        int hr = CoInitializeEx(IntPtr.Zero, COINIT_APARTMENTTHREADED);\n        if (hr != 0)\n        {\n            Marshal.ThrowExceptionForHR(hr);\n        }\n\n        try\n        {\n            MMAppInitialize mmAppInitialize = new MMAppInitialize();\n            mmLicenseStatus licenseStatus = mmAppInitialize.IsProductCodeAvailable(mmLicensedProductCode.mmLPDesigner);\n\n            if (licenseStatus == mmLicenseStatus.mmLicenseAvailable)\n            {\n                mmAppInitialize.Initialize(mmLicensedProductCode.mmLPDesigner);\n            }\n        }\n        finally\n        {\n            // Uninitialize COM\n            CoUninitialize();\n        }\n    }\n\n    // P/Invoke declarations\n    private const int COINIT_APARTMENTTHREADED = 0x2;\n\n    [DllImport("ole32.dll")]\n    private static extern int CoInitializeEx(IntPtr pvReserved, int dwCoInit);\n\n    [DllImport("ole32.dll")]\n    private static extern void CoUninitialize();\n}