using System.Data.SqlClient;\n\nstring connectionString = "your_connection_string_here";\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    // Execute the CREATE TABLE statement\n    string createTableQuery = "CREATE TABLE TableName (Id INT IDENTITY(1,1) PRIMARY KEY, Name NVARCHAR(50))";\n    using (SqlCommand createTableCommand = new SqlCommand(createTableQuery, connection))\n    {\n        createTableCommand.ExecuteNonQuery();\n    }\n\n    // Execute the SET IDENTITY INSERT statement\n    string setIdentityInsertQuery = "SET IDENTITY INSERT TableName ON";\n    using (SqlCommand setIdentityInsertCommand = new SqlCommand(setIdentityInsertQuery, connection))\n    {\n        setIdentityInsertCommand.ExecuteNonQuery();\n    }\n\n    // You can now insert data into the table with identity values specified\n    string insertDataQuery = "INSERT INTO TableName (Id, Name) VALUES (1, 'John Doe')";\n    using (SqlCommand insertDataCommand = new SqlCommand(insertDataQuery, connection))\n    {\n        insertDataCommand.ExecuteNonQuery();\n    }\n\n    // Don't forget to turn off IDENTITY_INSERT when done\n    string resetIdentityInsertQuery = "SET IDENTITY INSERT TableName OFF";\n    using (SqlCommand resetIdentityInsertCommand = new SqlCommand(resetIdentityInsertQuery, connection))\n    {\n        resetIdentityInsertCommand.ExecuteNonQuery();\n    }\n}